"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[8388],{494:e=>{e.exports=JSON.parse('{"version":{"pluginId":"user","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Introduction","href":"/alchemix-v3-docs/user/","className":"sidebarBold","docId":"index","unlisted":false},{"type":"link","label":"Quick Start","href":"/alchemix-v3-docs/user/quick-start","docId":"quick-start","unlisted":false},{"type":"link","label":"Links & Resources","href":"/alchemix-v3-docs/user/links","docId":"links","unlisted":false},{"type":"category","label":"Key Concepts","className":"sidebarBold","collapsed":false,"items":[{"type":"link","label":"Meta-Yield Token","href":"/alchemix-v3-docs/user/concepts/myt-and-yield","docId":"concepts/myt-and-yield","unlisted":false},{"type":"link","label":"Borrowing In Alchemix","href":"/alchemix-v3-docs/user/concepts/borrowing-in-alchemix","docId":"concepts/borrowing-in-alchemix","unlisted":false},{"type":"link","label":"Self-Repaying Loans","href":"/alchemix-v3-docs/user/concepts/self-repaying-loans","docId":"concepts/self-repaying-loans","unlisted":false},{"type":"link","label":"Redemption Rate","href":"/alchemix-v3-docs/user/concepts/redemption-rate","docId":"concepts/redemption-rate","unlisted":false},{"type":"link","label":"alAssets","href":"/alchemix-v3-docs/user/concepts/alAssets","docId":"concepts/alAssets","unlisted":false},{"type":"link","label":"Fixed-Duration Redemptions","href":"/alchemix-v3-docs/user/concepts/transmuter","docId":"concepts/transmuter","unlisted":false},{"type":"link","label":"Fees","href":"/alchemix-v3-docs/user/concepts/fees","docId":"concepts/fees","unlisted":false},{"type":"link","label":"Liquidations","href":"/alchemix-v3-docs/user/concepts/liquidations","docId":"concepts/liquidations","unlisted":false}],"collapsible":true},{"type":"category","label":"Tutorials","className":"sidebarBold","collapsed":false,"items":[{"type":"link","label":"Passive MYT Yield","href":"/alchemix-v3-docs/user/tutorials/use-passive-myt","docId":"tutorials/use-passive-myt","unlisted":false},{"type":"link","label":"Deposit & Borrow","href":"/alchemix-v3-docs/user/tutorials/deposit-and-borrow","docId":"tutorials/deposit-and-borrow","unlisted":false},{"type":"link","label":"Repay Loans","href":"/alchemix-v3-docs/user/tutorials/repay-loan","docId":"tutorials/repay-loan","unlisted":false},{"type":"link","label":"Redeem alAssets","href":"/alchemix-v3-docs/user/tutorials/redeem-alassets","docId":"tutorials/redeem-alassets","unlisted":false}],"collapsible":true},{"type":"category","label":"Guides","className":"sidebarBold","collapsed":false,"items":[{"type":"link","label":"How Peg Is Maintained","href":"/alchemix-v3-docs/user/newguides/how-peg-is-maintained","docId":"newguides/how-peg-is-maintained","unlisted":false},{"type":"link","label":"Risk Considerations","href":"/alchemix-v3-docs/user/newguides/risk-considerations","docId":"newguides/risk-considerations","unlisted":false}],"collapsible":true},{"type":"link","label":"FAQ","href":"/alchemix-v3-docs/user/faq","docId":"faq","unlisted":false}]},"docs":{"concepts/alAssets":{"id":"concepts/alAssets","title":"alAssets","description":"alAssets (alUSD, alETH) are synthetic tokens that mirror the value of their underlying asset. They play two roles:","sidebar":"tutorialSidebar"},"concepts/borrowing-in-alchemix":{"id":"concepts/borrowing-in-alchemix","title":"borrowing-in-alchemix","description":"After converting ETH or USDC into the Meta-Yield Token, the next step is borrowing. The vault keeps your collateral and lets you mint synthetic assets\u2014alETH or alUSD respectively\u2014worth up to ninety percent of the collateral\u2019s face value.","sidebar":"tutorialSidebar"},"concepts/fees":{"id":"concepts/fees","title":"fees","description":"Alchemix v3 keeps the cost structure transparent and predictable. Three parameters, each set by on-chain governance, clever every action a user can take. A redemption fee for borrowers, an optional redemption fee for Transmuter depositors, and a performance fee on Meta-Yield Token yield. No other fees apply.","sidebar":"tutorialSidebar"},"concepts/liquidations":{"id":"concepts/liquidations","title":"liquidations","description":"Liquidations in Alchemix v3 are a system-wide safety valve, not a per-account punishment. Because loans and collateral are like-kind, with ETH backing alETH and USDC backing alUSD, market price swings do not force positions to close. The back-stop only activates if the Meta-Yield Token itself loses backing.","sidebar":"tutorialSidebar"},"concepts/myt-and-yield":{"id":"concepts/myt-and-yield","title":"myt-and-yield","description":"Meta Yield Token (MYT) gives you passive exposure to a curated set of yield strategies without needing to manage positions yourself. Each token represents a share of assets that Alchemix allocates across multiple protocols.","sidebar":"tutorialSidebar"},"concepts/redemption-rate":{"id":"concepts/redemption-rate","title":"redemption-rate","description":"Redemptions convert earmarked collateral into the asset required to repay debt. Two ideas matter:","sidebar":"tutorialSidebar"},"concepts/self-repaying-loans":{"id":"concepts/self-repaying-loans","title":"self-repaying-loans","description":"A self-repaying loan lets you unlock liquidity without immediately selling your core position.","sidebar":"tutorialSidebar"},"concepts/transmuter":{"id":"concepts/transmuter","title":"transmuter","description":"The Transmuter lets you redeem alAssets (alUSD, alETH) 1:1 for their underlying asset after a known waiting period. Purchase below face value, and receive the full value on the maturity date.","sidebar":"tutorialSidebar"},"faq":{"id":"faq","title":"faq","description":"\\\\","sidebar":"tutorialSidebar"},"index":{"id":"index","title":"index","description":"QUICK START:","sidebar":"tutorialSidebar"},"links":{"id":"links","title":"links","description":"This documentation is the most complete learning resource, but it may lag behind day-to-day activity. Use the channels below to stay current and connect with the community.","sidebar":"tutorialSidebar"},"newguides/how-peg-is-maintained":{"id":"newguides/how-peg-is-maintained","title":"how-peg-is-maintained","description":"alAssets are the synthetic debt tokens in which loans are denominated, which are soft-pegged rather than hard-pegged. The protocol does not forcibly fix their price at 1.00 but relies on market incentives and redemption mechanics to pull the price back toward parity after short-term drifts.","sidebar":"tutorialSidebar"},"newguides/risk-considerations":{"id":"newguides/risk-considerations","title":"risk-considerations","description":"Protocol Trust & Risk Mitigation","sidebar":"tutorialSidebar"},"quick-start":{"id":"quick-start","title":"quick-start","description":"This page gives you a low-risk way to see Alchemix at work. It will walk through a small deposit, a modest borrow, and what to expect as redemptions reduce your balance.","sidebar":"tutorialSidebar"},"tutorials/deposit-and-borrow":{"id":"tutorials/deposit-and-borrow","title":"deposit-and-borrow","description":"A single vault deposit gives you immediate, auto-compounded yield through the Meta-Yield Token (MYT). From there you can:","sidebar":"tutorialSidebar"},"tutorials/redeem-alassets":{"id":"tutorials/redeem-alassets","title":"redeem-alassets","description":"The Earn page lists every fixed-rate term available on your current network. By depositing alAssets, or letting the interface swap ETH or USDC into their alAsset form, you lock in a known return that settles on the term\u2019s maturity date.","sidebar":"tutorialSidebar"},"tutorials/repay-loan":{"id":"tutorials/repay-loan","title":"repay-loan","description":"Why Repay?","sidebar":"tutorialSidebar"},"tutorials/use-passive-myt":{"id":"tutorials/use-passive-myt","title":"use-passive-myt","description":"The Meta-Yield Token turns a single deposit of ETH or UDSC into an auto-compounding position. Once minted, every MYT represents a growing claim on the underlying asset. No extra clicks are required to harvest or reinvest.","sidebar":"tutorialSidebar"}}}}')}}]);